<?php

namespace App\Livewire\Backend\Traits\GetImgUrl;


trait WithGetImgUrl
{
    // 取得圖片路徑如果沒有就回傳預設圖片
    static public function getImageUrl($imgPath)
    {
        if (!isset($imgPath) || $imgPath == null || $imgPath == '') {
            return self::base64Image();
        } else {
            return asset('storage/' . $imgPath);
        }
    }

    // 取得預設圖片
    static private function base64Image()
    {
        return "data:image/png;base64,";
    }
}
